module.header:
/** LittleFS on MXIC QSPI Flash Instance. */
extern const rm_littlefs_instance_t ${module.middleware.littlefs_mxic_qspi_flash.name};
extern rm_littlefs_flash_instance_ctrl_t ${module.middleware.littlefs_mxic_qspi_flash.name}_ctrl;
extern const rm_littlefs_cfg_t ${module.middleware.littlefs_mxic_qspi_flash.name}_cfg;

extern struct lfs ${module.middleware.littlefs_mxic_qspi_flash.name}_lfs;
extern const struct lfs_config ${module.middleware.littlefs_mxic_qspi_flash.name}_lfs_cfg;

module.declarations:
rm_littlefs_flash_instance_ctrl_t ${module.middleware.littlefs_mxic_qspi_flash.name}_ctrl;

#ifdef LFS_NO_MALLOC
static uint8_t ${module.middleware.littlefs_mxic_qspi_flash.name}_read[${module.middleware.littlefs_mxic_qspi_flash.cache_size}];
static uint8_t ${module.middleware.littlefs_mxic_qspi_flash.name}_prog[${module.middleware.littlefs_mxic_qspi_flash.cache_size}];
static uint8_t ${module.middleware.littlefs_mxic_qspi_flash.name}_lookahead[${module.middleware.littlefs_mxic_qspi_flash.lookahead_size}];
#endif

struct lfs ${module.middleware.littlefs_mxic_qspi_flash.name}_lfs;

const struct lfs_config ${module.middleware.littlefs_mxic_qspi_flash.name}_lfs_cfg =
{
    .context = &${module.middleware.littlefs_mxic_qspi_flash.name}_ctrl,
    .read    = &rm_littlefs_flash_read,
    .prog    = &rm_littlefs_flash_write,
    .erase   = &rm_littlefs_flash_erase,
    .sync    = &rm_littlefs_flash_sync,
    .read_size      = ${module.middleware.littlefs_mxic_qspi_flash.read_size},
    .prog_size      = ${module.middleware.littlefs_mxic_qspi_flash.prog_size},
    .block_size     = ${module.middleware.littlefs_mxic_qspi_flash.block_size},
    .block_count    = ${module.middleware.littlefs_mxic_qspi_flash.block_count},
    .block_cycles   = ${module.middleware.littlefs_mxic_qspi_flash.block_cycles},
    .cache_size     = ${module.middleware.littlefs_mxic_qspi_flash.cache_size},
    .lookahead_size = ${module.middleware.littlefs_mxic_qspi_flash.lookahead_size},
#ifdef LFS_NO_MALLOC
    .read_buffer = (void *) ${module.middleware.littlefs_mxic_qspi_flash.name}_read,
    .prog_buffer = (void *) ${module.middleware.littlefs_mxic_qspi_flash.name}_prog,
    .lookahead_buffer = (void *) ${module.middleware.littlefs_mxic_qspi_flash.name}_lookahead,

#endif
};

const rm_littlefs_flash_cfg_t ${module.middleware.littlefs_mxic_qspi_flash.name}_ext_cfg =
{
    .p_qspi = &${module.middleware.littlefs_mxic_qspi_flash.requires.r_qspi::module.driver.qspi.name},
};

const rm_littlefs_cfg_t ${module.middleware.littlefs_mxic_qspi_flash.name}_cfg =
{
    .p_lfs_cfg    = &${module.middleware.littlefs_mxic_qspi_flash.name}_lfs_cfg,
    .p_extend     = &${module.middleware.littlefs_mxic_qspi_flash.name}_ext_cfg,
};

/* Instance structure to use this module. */
const rm_littlefs_instance_t ${module.middleware.littlefs_mxic_qspi_flash.name} =
{
    .p_ctrl        = &${module.middleware.littlefs_mxic_qspi_flash.name}_ctrl,
    .p_cfg         = &${module.middleware.littlefs_mxic_qspi_flash.name}_cfg,
    .p_api         = &g_rm_littlefs_on_flash,
};
